import Editor from "@monaco-editor/react";
import { useState } from "react";

function App() {
  const [code, setCode] = useState("");
  const [language, setLanguage] = useState("python");
  const [output, setOutput] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const SubmitCode = async () => {
    setIsLoading(true);
    setOutput("⏳ Compiling...");

    try {
      const payload = {
        httpMethod: "POST",
        body: JSON.stringify({
          language: language.toLowerCase(),
          code: code,
        }),
      };

      const response = await fetch(
        "/2015-03-31/functions/function/invocations",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(payload),
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP Error: ${response.status}`);
      }

      const data = await response.json();
      const parsed =
        typeof data.body === "string" ? JSON.parse(data.body) : data.body;
      setOutput(parsed.message || "⚠️ No output received.");
    } catch (err) {
      setOutput("❌ Error: " + err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div
      className="min-h-screen bg-gray-100 p-6"
      style={{ fontFamily: "'Inter', sans-serif" }}
    >
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">
          Online Code Compiler
        </h1>

        {/* Language Selector and Submit Button */}
        <div className="flex items-center mb-4">
          {["Python", "JavaScript", "Java"].map((lang) => (
            <button
              key={lang}
              className={`px-4 py-2 mr-2 rounded-md text-sm font-medium transition-all duration-200 ${
                language === lang
                  ? "bg-blue-600 text-white"
                  : "bg-white text-gray-700 border border-gray-300 hover:bg-gray-50"
              }`}
              onClick={() => setLanguage(lang)}
            >
              {lang}
            </button>
          ))}
          <button
            className={`px-4 py-2 ml-4 rounded-md text-sm font-medium text-white transition-all duration-200 ${
              isLoading
                ? "bg-gray-500 cursor-not-allowed"
                : "bg-green-600 hover:bg-green-700"
            }`}
            onClick={SubmitCode}
            disabled={isLoading}
          >
            {isLoading ? "Compiling..." : "Run Code"}
          </button>
        </div>

        {/* Split Layout for Editor and Output */}
        <div
          className="flex gap-4"
          style={{ height: "calc(100vh - 160px)", minHeight: "400px" }}
        >
          {/* Code Editor */}
          <div className="flex-1">
            <div
              className="border border-gray-200 rounded-lg overflow-hidden"
              style={{ height: "100%" }}
            >
              <Editor
                height="100%"
                theme="vs-dark"
                language={language.toLowerCase()}
                value={code}
                defaultValue="// Write your code here"
                onChange={(value) => setCode(value)}
                options={{
                  minimap: { enabled: false },
                  fontSize: 14,
                  scrollBeyondLastLine: false,
                  automaticLayout: true,
                }}
              />
            </div>
          </div>

          {/* Output Display */}
          <div className="flex-1">
            <div
              className="bg-gray-900 text-white p-4 rounded-lg"
              style={{
                height: "100%",
                minHeight: "150px",
                whiteSpace: "pre-wrap",
              }}
            >
              <h3 className="text-lg font-semibold mb-2">Output:</h3>
              <pre className="text-sm">{output}</pre>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
