import logo from "./logo.svg";
import "./App.css";
import Editor from "@monaco-editor/react";
import { useState } from "react";

function App() {
  const [code, setCode] = useState("");
  const [language, setLanguage] = useState("");
  return (
    <div className="App">
      <button
        style={{
          background: language == "Python" ? "Black" : "White",
          color: language == "Python" ? "White" : "Black",
        }} // This for color change on click
        onClick={() => {
          setLanguage("Python");
        }} // This is for setting the language to Python
      >
        Python
      </button>

      <button
        style={{
          background: language == "JavaScript" ? "Black" : "White",
          color: language == "JavaScript" ? "White" : "Black",
        }} // This for color change on click
        onClick={() => {
          setLanguage("JavaScript");
        }} // This is for setting the language to JavaScript
      >
        JavaScript
      </button>

      <button
        style={{
          background: language == "Java" ? "Black" : "White",
          color: language == "Java" ? "White" : "Black",
        }} // This for color change on click
        onClick={() => {
          setLanguage("Java");
        }} // This is for setting the language to Java
      >
        Java
      </button>

      <Editor
        onChange={(e) => {
          debugger;
          console.log(setCode(e.target.value)) // this is the value of the editor
        }}
        value={code} // this is the value of the editor
        theme="vs-dark" // this is added by me:- "to set the theme to dark"
        height="90vh"
        defaultLanguage="javascript"
        defaultValue="// some comment" // this is default text on opening the editor
      />
      {/* This Editor:Tag works as a compitent */}
    </div>
  );
}

export default App;
