import logo from "./logo.svg";
import "./App.css";
import Editor from "@monaco-editor/react";
import { useState } from "react";

function App() {
  const [code, setCode] = useState("");
  const [language, setLanguage] = useState("");
  return (
    <div className="App">
      <button style={{ background: language == "python" ? "Black" : "White" }}
      >
        Python
      </button>

      <button
        style={{ background: language == "JavaScript" ? "Black" : "White" }}
        onClick={() => {
          setLanguage("JavaScript");
        }}
      >
        JavaScript
      </button>

      <button style={{ background: language == "Java" ? "Black" : "White" }}>
        Java
      </button>

      <Editor
        theme="vs-dark" // this is added by me:- "to set the theme to dark"
        height="90vh"
        defaultLanguage="javascript"
        defaultValue="// some comment"
      />
      {/* This Editor:Tag works as a compitent */}
    </div>
  );
}

export default App;
