import { useState } from 'react';
import Editor from '@monaco-editor/react';

export default function AppleCodeCompiler() {
  const [language, setLanguage] = useState("Python");
  const [code, setCode] = useState("# Write your code here");
  const [output, setOutput] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const SubmitCode = () => {
    setIsLoading(true);
    // Simulate compilation
    setTimeout(() => {
      setOutput(`Executed ${language} code successfully.\n> Hello, world!`);
      setIsLoading(false);
    }, 1500);
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-6">
      {/* Header */}
      <header className="mb-6">
        <h1 className="text-3xl font-medium bg-gradient-to-r from-blue-500 to-purple-600 bg-clip-text text-transparent">
          Code
          <span className="font-bold">Compiler</span>
        </h1>
        <p className="text-gray-500 dark:text-gray-400 mt-1 text-sm">
          Write and compile code directly in your browser
        </p>
      </header>

      {/* Language Selector */}
      <div className="flex mb-4 gap-2 p-1 bg-gray-100 dark:bg-gray-800 rounded-xl w-fit">
        {["Python", "JavaScript", "Java", "Swift"].map((lang) => (
          <button
            key={lang}
            onClick={() => setLanguage(lang)}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${
              language === lang 
                ? "bg-white dark:bg-gray-700 shadow-sm text-blue-500 dark:text-blue-400" 
                : "text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
            }`}
          >
            {lang}
          </button>
        ))}
      </div>

      {/* Code Editor Container */}
      <div className="flex-grow relative rounded-xl overflow-hidden shadow-lg border border-gray-200 dark:border-gray-700">
        <Editor
          height="100%"
          theme="vs-dark"
          language={language.toLowerCase()}
          value={code}
          onChange={(value) => setCode(value || "")}
          options={{
            fontSize: 14,
            fontFamily: 'SF Mono, Menlo, Monaco, Consolas, monospace',
            minimap: { enabled: false },
            scrollBeyondLastLine: false,
            roundedSelection: true,
            padding: { top: 16 }
          }}
        />
      </div>

      {/* Controls and Output */}
      <div className="mt-4 flex flex-col lg:flex-row gap-4">
        {/* Run Button */}
        <div className="flex items-center lg:w-1/3">
          <button
            onClick={SubmitCode}
            disabled={isLoading}
            className={`rounded-full px-6 py-2.5 font-medium text-white transition-all ${
              isLoading
                ? "bg-gray-400 dark:bg-gray-600 cursor-not-allowed"
                : "bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 shadow-md hover:shadow-lg"
            }`}
          >
            {isLoading ? "Compiling..." : "Run Code"}
          </button>
        </div>

        {/* Output Display */}
        <div className="flex-grow bg-gray-100 dark:bg-gray-800 rounded-xl p-4 text-sm font-mono">
          <div className="flex items-center mb-2">
            <div className="w-3 h-3 rounded-full bg-red-500 mr-2"></div>
            <div className="w-3 h-3 rounded-full bg-yellow-500 mr-2"></div>
            <div className="w-3 h-3 rounded-full bg-green-500 mr-2"></div>
            <span className="text-xs text-gray-500 dark:text-gray-400 ml-2">Output</span>
          </div>
          <pre className="mt-2 whitespace-pre-wrap text-gray-700 dark:text-gray-300">
            {output || "Code output will appear here after execution."}
          </pre>
        </div>
      </div>
    </div>
  );
}
