import "./App.css";
import Editor from "@monaco-editor/react";
import { useEffect, useState } from "react";

function App() {
  const [code, setCode] = useState("");
  const [language, setLanguage] = useState("python");
  const [output, setOutput] = useState("");

  // Handles code submission
  const SubmitCode = async () => {
    try {
      const response = await fetch(
        "http://localhost:9000/2015-03-31/functions/function/invocations",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            code: code,
            language: language.toLowerCase(), // Send language in lowercase
          }),
        }
      );

      const data = await response.json();
      setOutput(data.body); // Show backend output
    } catch (err) {
      setOutput("Error: " + err.message);
    }
  };

  return (
    <div className="App">
      <h2>Online Code Compiler</h2>

      {/* Language Buttons */}
      <div style={{ marginBottom: "10px" }}>
        {["Python", "JavaScript", "Java"].map((lang) => (
          <button
            key={lang}
            style={{
              marginRight: "10px",
              background: language === lang ? "black" : "white",
              color: language === lang ? "white" : "black",
              border: "1px solid black",
              padding: "8px 16px",
              borderRadius: "4px",
            }}
            onClick={() => setLanguage(lang)}
          >
            {lang}
          </button>
        ))}

        {/* Submit Button */}
        <button
          onClick={SubmitCode}
          style={{
            marginLeft: "20px",
            padding: "8px 16px",
            backgroundColor: "green",
            color: "white",
            border: "none",
            borderRadius: "4px",
          }}
        >
          Submit Code
        </button>
      </div>

      {/* Code Editor */}
      <Editor
        onChange={(value) => setCode(value)}
        value={code}
        theme="vs-dark"
        height="60vh"
        language={language.toLowerCase()} // Optional: dynamically change editor mode
        defaultValue="// Write your code here"
      />

      {/* Output Box */}
      <div
        style={{
          backgroundColor: "#1e1e1e",
          color: "#ffffff",
          padding: "10px",
          marginTop: "20px",
          minHeight: "100px",
          whiteSpace: "pre-wrap",
          borderRadius: "6px",
        }}
      >
        <h3>Output:</h3>
        <pre>{output}</pre>
      </div>
    </div>
  );
}

export default App;
